/* Pile - Bulk Action Link */$(document).on("click",".bulk-action",function(e){e.preventDefault();var t=$(this).data("action");if(t=="later"||t=="archive"||t=="trash"){var n="";$.url.segment(0)==="in"&&(n=$.url.segment(1));mailpile.tag_add_delete(t,n,mailpile.bulk_cache,function(){$.each(mailpile.bulk_cache,function(e,t){$("#pile-message-"+t).fadeOut("fast")});mailpile.bulk_cache=[]})}else t!="add-to-group"&&t=="tag"&&mailpile.tag_list(function(e){var t="",n="";$.each(e.tags,function(e,r){r.display==="tag"?t+='<li class="checkbox-item-picker" data-tid="'+r.tid+'" data-slug="'+r.slug+'"><input type="checkbox"> '+r.name+"</li>":r.display==="archive"&&(n+='<li class="checkbox-item-picker"><input type="checkbox"> '+r.name+"</li>")});var r=$("#modal-tag-picker").html();$("#modal-full").html(_.template(r,{tags:t,archive:n}));$("#modal-full").modal({backdrop:!0,keyboard:!0,show:!0,remote:!1})})});